#include QMK_KEYBOARD_H


enum {
    TMUX_LAYR = SAFE_RANGE,
};

bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        case TMUX_LAYR:
            if (record->event.pressed) {
                tap_code16(LCTL(KC_B));
            } else {
                return false;
            }
            return false;
        default:
            return true;
    }
}

